let lastScroll=0;const defaultoffset=100,header=document.querySelector(".header"),menu=document.querySelector(".header__nav"),scrollPosition=()=>window.pageYOffset||document.documentElement.scrollTop,containHide=()=>header.classList.contains("hide");window.addEventListener("scroll",()=>{scrollPosition()>lastScroll&&!containHide()&&scrollPosition()>100?(header.classList.add("hide"),menu.classList.remove("header__nav_active")):scrollPosition()<lastScroll&&containHide()?(menu.classList.remove("header__nav_active"),header.classList.remove("hide")):scrollPosition()<lastScroll&&menu.classList.remove("header__nav_active"),lastScroll=scrollPosition()}),function(){let a=document.querySelector(".burger"),d=document.querySelector(".header__nav"),b=document.querySelector(".header__nav-close"),c=document.querySelector(".main");a.addEventListener("click",()=>{d.classList.add("header__nav_active")}),b.addEventListener("click",()=>{d.classList.remove("header__nav_active")}),c.addEventListener("click",()=>{d.classList.remove("header__nav_active")})}()